import React from 'react';
import { useState } from 'react';

const App = () => {
  const purple = "#8a44ad";
  const [bg, setBg] = useState(purple);
  const [name, setName] = useState('Click Me');

  const colorChange = () => {
    let color = "#34495e";
    setBg(color);
    setName("Ouch ðŸ˜Ÿ");
  };

  const textChange = () => {
    // Toggle between "Ouch ðŸ˜Ÿ" and "WTF ðŸ¤¬"
    setName((prevName) => (prevName === "Ouch ðŸ˜Ÿ" ? "WTF ðŸ¤¬" : "Ouch ðŸ˜Ÿ"));
    let color = "#afafaf";
    setBg(color);
  };

  return (
    <>
      <div style={{ backgroundColor: bg }}>
        <button onClick={colorChange} onDoubleClick={textChange}>
          {name}
        </button>
      </div>
    </>
  );
};

export default App;
