import React from 'react';
import { useState } from 'react';

const App = () =>{

  const color = "#afafaf";
  const[bg, setBg] = useState(color);
  const[name, setName] = useState('Click Me');

  const colorChange = () =>{
    let color = "#8a44ad";
    setBg(color);
    let name = "Ouch ðŸ˜Ÿ"
  }
  return(
    <>
    <div style={ { backgroundColor: bg } }>
      <button onClick={colorChange}> {name} </button>
    </div>
    </>
  )
}

export default App;
