    let color = "#afafaf";
    setBg(color);
    setName("WTF ðŸ¤¬");
  };
  
  return(
    <>
    <div style={ { backgroundColor: bg } }>
      <button onKeyDown={colorChange} onMouseOut={textChange}> {name} </button>
    </div>
    </>
  )
}

export default App;
