import React from 'react';
import { useState } from 'react';

const App = () =>{

  const purple = "#8a44ad";
  const[bg, setBg] = useState(purple);
  const[name, setName] = useState('Click Me');

  const colorChange = () =>{
    let color = "#34495e";
    setBg(color);
    setName("Ouch ðŸ˜Ÿ");
  };

  const textChange = () =>{
    let color = "#afafaf";
    setBg(color);
    setName("WTF ðŸ¤¬");
  };
  
  return(
    <>
    <div style={ { backgroundColor: bg } }>
      <button onmouseover={colorChange} onDoubleClick={textChange}> {name} </button>
    </div>
    </>
  )
}

export default App;
